INSERT INTO `phoneadvice`.`phone` (`assessment`, `description`, `image`, `model`, `brand_id`, `gamma_id`,`statistic_id`) VALUES ('4', 'Es pequeño, es potente, el nuevo Xiaomi Redmi Go es la primera apuesta del fabricante Chino con Android Go, pensado para equipos economicos pero sin sacrificar potencia.', 'xiaomi/redmi_go', 'Redmi GO', '3', '1',1);
INSERT INTO `phoneadvice`.`phone` (`assessment`, `description`, `image`, `model`, `brand_id`, `gamma_id`,`statistic_id`) VALUES ('3', 'Una renovación hermosa para una familia de equipos fantastica.', 'xiaomi/mi_mix_3', 'Mi Mix 3', '3', '3',2);
INSERT INTO `phoneadvice`.`phone` (`assessment`, `description`, `image`, `model`, `brand_id`, `gamma_id`,`statistic_id`) VALUES ('6', 'Xiaomi se adelanta a todo el mundo y antes del lanzamiento del Samsung S10, presenta su nuevo buque de Guerra.', 'xiaomi/mi_9', 'Mi 9', '3', '3',3);
INSERT INTO `phoneadvice`.`phone` (`assessment`, `description`, `image`, `model`, `brand_id`, `gamma_id`,`statistic_id`) VALUES ('3', 'Económico y potente', 'xiaomi/redmi_6', 'Redmi 6', '3', '1',4);
INSERT INTO `phoneadvice`.`phone` (`assessment`, `description`, `image`, `model`, `brand_id`, `gamma_id`,`statistic_id`) VALUES ('2', 'Xiaomi sorprende con este celular de gama media con una cámara de 48 megapixeles', 'xiaomi/redmi_note_7', 'Redmi note 7', '3', '2',5);
INSERT INTO `phoneadvice`.`phone` (`assessment`, `description`, `image`, `model`, `brand_id`, `gamma_id`,`statistic_id`) VALUES ('1', 'El ZenFone Max Pro es tu incansable compañero, siempre preparado para capturar los momentos especiales.', 'asus/zenfone_max_pro', 'Zenfone Max Pro', '6', '2',6);
INSERT INTO `phoneadvice`.`phone` (`assessment`, `description`, `image`, `model`, `brand_id`, `gamma_id`,`statistic_id`) VALUES ('3', 'Diseñado para causar impresión, el ZenFone 5 está fabricado con materiales premium y técnicas de manufactura avanzadas. ', 'asus/zenfone_5', 'Zenfone 5', '6', '2',7);
INSERT INTO `phoneadvice`.`phone` (`assessment`, `description`, `image`, `model`, `brand_id`, `gamma_id`,`statistic_id`) VALUES ('6', 'El ZenFone 5Z realiza cualquier tarea con fluidez y, gracias a su elegante diseño y compactas dimensiones, usarlo es un auténtico lujo.', 'asus/zenfone_5z', 'Zenfone 5z', '6', '3',8);
INSERT INTO `phoneadvice`.`phone` (`assessment`, `description`, `image`, `model`, `brand_id`, `gamma_id`,`statistic_id`) VALUES ('7', 'El ZenFone 3 presume de un diseño ágil y exquisito inspirado en la belleza de la naturaleza. Es una obra de arte en metal y vidrio que captura el mundo con la máxima precisión. Un teléfono extraordinario para una vida menos ordinaria.', 'asus/zenfone_3', 'Zenfone 3', '6', '2',9);
INSERT INTO `phoneadvice`.`phone` (`assessment`, `description`, `image`, `model`, `brand_id`, `gamma_id`,`statistic_id`) VALUES ('1', 'Un celular económico, pero que no se quedará atrás al ejecutar tus aplicaciones', 'asus/zenfone_pegasus_4A', 'Zenfone Pegasus 4A', '6', '1',10);
INSERT INTO `phoneadvice`.`phone` (`assessment`, `description`, `image`, `model`, `brand_id`, `gamma_id`, `statistic_id`) VALUES ('6', 'El resultado de 10 años siendo pioneros en dispositivos móviles, Galaxy S10. La nueva generación Galaxy ha llegado.', 'samsung/galaxy_s10', 'Galaxy s10', '2', '3', 11);
INSERT INTO `phoneadvice`.`phone` (`assessment`, `description`, `image`, `model`, `brand_id`, `gamma_id`, `statistic_id`) VALUES ('7', 'Uno de los mejores teléfonos del año que gracias al increíble lápiz S Pen, el Galaxy Note9 continúa teniendo un diseño único.', 'samsung/galaxy_note_9', 'Galaxy note 9', '2', '3', 12);
INSERT INTO `phoneadvice`.`phone` (`assessment`, `description`, `image`, `model`, `brand_id`, `gamma_id`, `statistic_id`) VALUES ('5', 'La pantalla maximizada del Galaxy A70 te acerca aún más a lo que te importa: tus contenidos cobran vida en la pantalla Infinity-U FHD+ sAMOLED. Transmite vídeo o disfruta de tus series favoritas como nunca: la amplia relación de aspecto 20:9 brinda una experiencia sin igual y te transporta a un nuevo mundo de imagen', 'samsung/galaxy_a70', 'Galaxy A70', '2', '2',13);
INSERT INTO `phoneadvice`.`phone` (`assessment`, `description`, `image`, `model`, `brand_id`, `gamma_id`, `statistic_id`) VALUES ('3', 'Captura increíbles fotografías con el Galaxy A9, el primer Smartphone con cámara cuádruple. Sumérgete en su pantalla infinita de 6,3” y el envolvente sonido de Dolby Atmos. Su diseño elegante y ergonómico se sentirá perfecto en tus manos.', 'samsung/galaxy_a9', 'Galaxy A9', '2', '2', 14);
INSERT INTO `phoneadvice`.`phone` (`assessment`, `description`, `image`, `model`, `brand_id`, `gamma_id`, `statistic_id`) VALUES ('4', 'Lleva tu experiencia visual a otro nivel. El Galaxy J4 tiene una gran pantalla HD de 5.5" que beneficia la relación de contraste y la calidad de las imágenes. Ahora puedes disfrutar de tus contenidos multimedia favoritos con colores más vivos y naturales.', 'samsung/galaxy_j4', 'Galaxy J4', '2', '1', 15);
INSERT INTO `phoneadvice`.`phone` (`assessment`, `description`, `image`, `model`, `brand_id`, `gamma_id`, `statistic_id`) VALUES ('2', 'El arte es un proceso de refinamiento, de eliminación de todo lo que no se necesita hasta que te queda algo puro, y a la vez poderoso.Eso es Nokia 9 PureView.', 'nokia/9_pureview', '9 PureView', '8', '3', 16);
INSERT INTO `phoneadvice`.`phone` (`assessment`, `description`, `image`, `model`, `brand_id`, `gamma_id`, `statistic_id`) VALUES ('5', 'Exprésate y cuenta tu historia, ya sea que te encuentres al aire libre, con sol pleno, o en una habitación tenuemente iluminada, el Nokia 7.1 y su pantalla se adaptarán dinámicamente al entorno para una espectacular experiencia dentro o afuera.', 'nokia/7_1', 'Nokia 7.1', '8', '2', 17);
INSERT INTO `phoneadvice`.`phone` (`assessment`, `description`, `image`, `model`, `brand_id`, `gamma_id`, `statistic_id`) VALUES ('2', 'Un teléfono con el que puedes contar.Comienza con un exigente proceso de esculpido que transforma un solo bloque de aluminio sólido en el monobloque de un smartphone duradero que puedes llevar a cualquier parte.', 'nokia/6_1', 'Nokia 6.1', '8', '2', 18);
INSERT INTO `phoneadvice`.`phone` (`assessment`, `description`, `image`, `model`, `brand_id`, `gamma_id`, `statistic_id`) VALUES ('4', 'Tu compañero de primera.Nokia 3.1 se ve y se siente superior. Llama la atención con sus sutiles curvas y sorprendentes lados de aluminio de corte de diamante. El nuevo Nokia 3 se siente cómodo en la mano gracias a una parte trasera curvada y una pantalla esculpida Corning® Gorilla® Glass.', 'nokia/3_1', 'Nokia 3.1', '8', '1', 19);
INSERT INTO `phoneadvice`.`phone` (`assessment`, `description`, `image`, `model`, `brand_id`, `gamma_id`, `statistic_id`) VALUES ('1', 'Entretenimiento de larga duración. Accionado por una batería2 4000 mAh2, Nokia 2.1 se mantiene trabajando por dos días seguidos, así que puedes pasar menos tiempo en preocuparte por cargarlo y más tiempo haciendo lo que amas.', 'nokia/2_1', 'Nokia 2.1', '8', '1', 20);
