INSERT INTO `phoneadvice`.`phone` (`assessment`, `description`, `image`, `model`, `brand_id`, `gamma_id`, `statistic_id` ) VALUES ('4', 'Más brillo, amplitud y cercania. Observa el mundo desde una nueva perspectiva. Descubre las sorpresas ocultas y conviértelas en tus recuerdos más preciados. El HUAWEI P30 redefine la fotografía en teléfonos inteligentes.', 'huawei/p30', 'P30', '5', '3','7');
INSERT INTO `phoneadvice`.`phone` (`assessment`, `description`, `image`, `model`, `brand_id`, `gamma_id`, `statistic_id` ) VALUES ('2', 'Una obra de arte que combina tecnología y diseño. La elegante pantalla sin bordes del HUAWEI Mate 20 acentúa su belleza. Su poderoso mecanismo crea un camino nuevo a la Inteligencia Artificial, y abre las puertas a posiblilidades infinitas.', 'huawei/mate_20', 'Mate 20', '5', '3','6');
